    private int totalTime = 25 * 60;
    private Timer timer;

    private string formattedTimer => TimeSpan.FromSeconds(totalTime).ToString(@"mm\:ss");

    private void Start()
    {
        timer?.Dispose();
        timer = new Timer(_ =>
        {
            if (totalTime > 0)
            {
                totalTime -= 1;
                InvokeAsync(StateHasChanged);
            }
        }, null, 0, 1000);
    }

    private void Reset()
    {
        timer?.Dispose();
        totalTime = 25 * 60;
    }

    private void Stop()
    {
        timer?.Dispose();
    }

    public void Dispose()
    {
        timer?.Dispose();
    }

